-- ---------------------------------------------------------------------------
-- CITATION_COLLECTION
-- ---------------------------------------------------------------------------

CREATE TABLE CITATION_COLLECTION
(
	COLLECTION_ID VARCHAR (36) NOT NULL,
	PROPERTY_NAME VARCHAR (255),
	PROPERTY_VALUE LONGTEXT
--	CONSTRAINT CITATION_COLLECTION_INDEX (COLLECTION_ID)
);

-- ---------------------------------------------------------------------------
-- CITATION_CITATION
-- ---------------------------------------------------------------------------

CREATE TABLE CITATION_CITATION
(
	CITATION_ID VARCHAR (36) NOT NULL,
	PROPERTY_NAME VARCHAR (255),
	PROPERTY_VALUE LONGTEXT
--	CONSTRAINT CITATION_CITATION_INDEX (CITATION_ID)
);

-- ---------------------------------------------------------------------------
-- CITATION_COLLECTION_ORDER
-- ---------------------------------------------------------------------------
CREATE TABLE CITATION_COLLECTION_ORDER
(
	COLLECTION_ID VARCHAR (36) NOT NULL,
	CITATION_ID VARCHAR (36),
	SECTION_TYPE ENUM('HEADING1','HEADING2', 'HEADING3', 'DESCRIPTION', 'CITATION'),
	VALUE TEXT,
	LOCATION INT NOT NULL
);

-- ---------------------------------------------------------------------------
-- CITATION_SCHEMA
-- ---------------------------------------------------------------------------
CREATE TABLE CITATION_SCHEMA
(
	SCHEMA_ID VARCHAR (36) NOT NULL,
	PROPERTY_NAME VARCHAR (255),
	PROPERTY_VALUE LONGTEXT
--	CONSTRAINT CITATION_SCHEMA_INDEX (SCHEMA_ID)
);

-- ---------------------------------------------------------------------------
-- CITATION_SCHEMA_FIELD
-- ---------------------------------------------------------------------------
CREATE TABLE CITATION_SCHEMA_FIELD
(
    SCHEMA_ID VARCHAR (36) NOT NULL,
    FIELD_ID VARCHAR (36) NOT NULL,
	PROPERTY_NAME VARCHAR (255),
	PROPERTY_VALUE LONGTEXT
--	CONSTRAINT CITATION_SCHEMA_INDEX (SCHEMA_ID, FIELD_ID)
);

create index CITATION_COLLECTION_INDEX on CITATION_COLLECTION (COLLECTION_ID);
create index CITATION_CITATION_INDEX on CITATION_CITATION (CITATION_ID);
create index CITATION_COLLECTION_ORDER_INDEX on CITATION_COLLECTION_ORDER (COLLECTION_ID,CITATION_ID);
create index CITATION_SCHEMA_INDEX on CITATION_SCHEMA (SCHEMA_ID);
create index CITATION_SCHEMA_FIELD_INDEX on CITATION_SCHEMA_FIELD (SCHEMA_ID, FIELD_ID);

create index CITATION_COLLECTION_INDEX2 on CITATION_COLLECTION (COLLECTION_ID, PROPERTY_NAME);
create index CITATION_CITATION_INDEX2 on CITATION_CITATION (CITATION_ID, PROPERTY_NAME);
create index CITATION_SCHEMA_INDEX2 on CITATION_SCHEMA (SCHEMA_ID, PROPERTY_NAME);
create index CITATION_SCHEMA_FIELD_INDEX2 on CITATION_SCHEMA_FIELD (SCHEMA_ID, FIELD_ID, PROPERTY_NAME);



